#!/bin/bash
jlink --no-header-files --no-man-pages --strip-debug --strip-native-commands --module-path "$JAVA_HOME:$JAVAFX_HOME/jmods" --add-modules "java.base,java.compiler,java.datatransfer,java.desktop,java.logging,java.management,java.naming,java.prefs,java.rmi,java.scripting,java.sql,java.xml,javafx.base,javafx.controls,javafx.fxml,javafx.graphics,javafx.swing,jdk.unsupported,jdk.xml.dom" --output target/build/bundle/runtime
